                          style={{
                            background: `linear-gradient(45deg, ${picker1Color}, ${picker2Color})`,
                          }}
                        ></div>
                        <div>
                          Primary{" "}
                          <ColorPicker
                            defaultValue={picker1Color}
                            value={picker1Color}
                            onChange={(color) =>
                              setPicker1Color(`#${color.hex}`)
                            }
                            deferred
                            disableAlpha
                          />
                          Secondary{" "}
                          <ColorPicker
                            defaultValue={picker2Color}
                            value={picker2Color}
                            onChange={(color) =>
                              setPicker2Color(`#${color.hex}`)
                            }
                            deferred
                            disableAlpha
                          />
                        </div>
                        <div className="fontpicker-div">
                        <div className="fontpicker-label">Font</div>
                          <Select
                            value={fontFamily}
                            variant="standard"
                            onChange={(e) => setFontFamily(e.target.value)}
                            className="fontpicker"
                            autoWidth={true}
                          >
                            {fonts.map((font, idx) => (
                              <MenuItem value={font} key={idx}>
                                {font}
                              </MenuItem>
                            ))}
                          </Select>
